import React, { useRef, useState } from "react";
import { Link } from "react-router-dom";
import { Dropdown } from "react-bootstrap";

function Navbar() {
  const navBarReact = useRef(null); // create a ref using useRef hook
  const [searchValue, setSearchValue] = useState("");

  function handleSearchInputChange() {
    setSearchValue(navBarReact.current.value);
  }

  return (
    <div>
      <nav className="navbar navbar-expand-md sticky-top bg-white m-0 py-0">
        <img
          className="rounded d-block mx-4"
          alt=""
          id="logo-img"
          src={require("../images/photo_2023-04-13 23.51.37.jpeg")}
        />
        <a className="navbar-brand">Bookish</a>
        <form className="d-flex mx-auto searchSection" role="search" id="searchFormNavbar">
          <input
            ref={navBarReact}
            id="search-bar"
            className="form-control me-2 mx-2"
            type="search"
            placeholder="Search"
            aria-label="Search"
            onChange={handleSearchInputChange}
          ></input>
          <Link
            id="search-button"
            className="btn btn-outline-success mx-auto"
            to={`/search?input=${searchValue}`}
          >
            Search
          </Link>
        </form>
        <Dropdown className="mx-4">
          <Dropdown.Toggle id="dropdown-pfp">
            <img id="pfp-img" className="rounded d-block" alt="" />
          </Dropdown.Toggle>
          <Dropdown.Menu>
            <Dropdown.Item href="#">Change Avatar</Dropdown.Item>
            <Dropdown.Item href="#">Create Book</Dropdown.Item>
          </Dropdown.Menu>
        </Dropdown>
      </nav>
    </div>
  );
}

export default Navbar;
