import React, { useState, useEffect } from "react";
import { useLocation } from "react-router-dom";
import Filter from "./components/Filter";
import ContentSearch from "./components/ContentSearch";

function Search() {
  const [books, setBooks] = useState([]);
  const [hasMounted, setHasMounted] = useState(false);
  const [booksFromFilter, setBooksFromFilter] = useState([]);
  const location = useLocation();
  const input = new URLSearchParams(location.search).get('input');

  useEffect(() => {
    if (input != null && !hasMounted) {
      fetch(`http://localhost:4000/books/${input}`)
        .then((response) => response.json())
        .then((data) => {
          setBooks(data);
          setHasMounted(true);
        });
    }
  }, [input, hasMounted]);

  function updateBooks(newBooks) {
    setBooksFromFilter(newBooks);
    setHasMounted(false);
  }

  return (
    <div id='main-search'>
      <Filter updateBooks={updateBooks} />
      <ContentSearch books={hasMounted ? books : booksFromFilter} />
    </div>
  );
}

export default Search;
